    const [SignUpData, setSignUpData] = useState(dataSignUp)
    const [error, setError] = useState('')
    const [success, setSuccess] = useState('')

    const {pseudo, email, password, confirmPassword} = SignUpData


const authData={
    email: email,
    password: password,
    returnSecureToken: true
}



const submit=(e)=>{
e.preventDefault()
const userData=[{
    pseudo : pseudo,
    email : email
}]


axios.post(`https://marvel-quiz-cb809.firebaseio.com/users/${pseudo}.json`,userData).then(response=>{
    console.log(response)
}).catch(err=>{
    console.log(err)
})
