            <div className='inputBox'>
                <input type='email' value={email} id='email' required onChange={valueInput} />
                <label htmlFor='email'>Email</label>
            </div>
            <div className='inputBox'>
                <input type='password' value={password} id='password' required onChange={valueInput} />
