import React,{useState} from 'react'


function Inscription() {
    const [valueForm, setValueForm] = useState(

        identiter =[{

        pseudo:'',
        email:'',
        password:'' }] 

)


const valider=(e)=>{
e.preventDefault()

}

const valueInput=(e)=>{

const id = e.target.id;
const value = e.target.value

switch (id) {
case 'pseudo':
    setValueForm({pseudo : value })

    break;
case 'email':

    break;
case 'password':

    break;

default:
    break;
}

}



return (
<div className='signUpLoginBox'>
<div className='slContainer'>
    <div className='formBoxLeftSignup'>

    </div>
    <div className='formBoxRight'>
        <form className='formContent' onSubmit={valider} >

            <h2>Inscription</h2>
            <div className='inputBox'>
                <input type='text' id='pseudo' required  onChange={valueInput} />
                <label htmlFor='pseudo'>Pseudo</label>
            </div>
            <div className='inputBox'>
                <input type='text' id='email' required onChange={valueInput} />
                <label htmlFor='email'>Email</label>
            </div>
            <div className='inputBox'>
                <input type='text' id='password' required onChange={valueInput} />
                <label htmlFor='password'>password</label>
            </div>
            <div className='inputBox'>
                <input type='text' id='confirmPassword' required  onChange={valueInput}/>
                <label htmlFor='confirmPassword'>confirmer votre mot de passe</label>
            </div>

            <div>
                <button className='btnSubmit'>Inscription</button>
            </div>


        </form>
    </div>
</div>

</div>
)
}
export default Inscription
