import React,{Component} from 'react'
import Levels from '../Levels/index'
import ProgressBar from '../ProgressBar/index'
import {QuizMarvel} from '../QuizMarvel/index'

class Quiz extends Component {

    state={
       levelName:['debutant','confirme','expert'],
       QuizLevel: 0,
       maxQuestion: 10,
       storedQuestion:[],
       questions: '',
       options: [],
       idQuestion: 0,
       btnValue: true,
       userAnswer:null,

    }

    loadQuestion= level =>{
        const fetchArrayQuiz = QuizMarvel[0].quizz[level]
        const lengthArray = fetchArrayQuiz.length

        if(lengthArray >= this.state.maxQuestion){

           const newArray = fetchArrayQuiz.map( ({answer, ...keepRest}) =>keepRest)
            this.setState({storedQuestion : newArray})

        }
    }  

    ValueSelected =(ValueSelected)=>{
        this.setState({
            userAnswer : ValueSelected
        })
        console.log(ValueSelected)
    }


    componentDidMount(){
        this.loadQuestion(this.state.levelName[this.state.QuizLevel])
    }

    componentDidUpdate(prevProps , prevState){
        if(this.state.storedQuestion !== prevState.storedQuestion){
            this.setState({
                questions : this.state.storedQuestion[this.state.idQuestion].question,
                options : this.state.storedQuestion[this.state.idQuestion].options,
            })
        }
    }


    render(){

        const {options, questions, btnValue} = this.state

            const displayOption = options.map( (option,index) =>{
            return <p key={index} onClick={()=> this.ValueSelected(option)} className={`answerOptions ${this.state.answer === option ? 'selected': null}`}> {option}</p> })

        return (
            <div>
                <Levels />
                <ProgressBar />
                <h2>{questions}</h2>
                {displayOption}
                 <button disabled={btnValue} className='btnSubmit'>Suivant</button>

            </div> 
        )    

        }
 }
export default Quiz
