import React,{useEffect,useState,useRef,Fragment} from 'react'

function Landing() {

    const [btn,setBtn]= useState(false)
    const refwolf = useRef(null)

    const showGriffe =()=>{
        refwolf.current.classList.add('leftImg')

    }

    useEffect(()=>{
        refwolf.current.classList.add('startingImg')
        setTimeout(() => {
        refwolf.current.classList.remove('startingImg')
        setBtn(true)
        }, 1000);
    },[])




   const showBtn= btn && ( 
            <Fragment>
            <div onMouseOver={showGriffe} className='leftBox'>
            <button className='btn-welcome'>Inscription</button>
            </div>
            <div  onMouseOver={showGriffe} className='rightBox'> 
            <button className='btn-welcome'>Connexion</button>
            </div>
            </Fragment>)

    return (
        <main ref={refwolf} className='welcomePage'>

        {showBtn}

        </main>
    )
}

export default Landing
