import app from 'firebase/app'
import 'firebase/auth'


var config = {
    apiKey: "AIzaSyAa-FqTe2diLWwLcCH3kLLXAbol0UYcodQ",
    authDomain: "marvel-quiz-cb809.firebaseapp.com",
    databaseURL: "https://marvel-quiz-cb809.firebaseio.com",
    projectId: "marvel-quiz-cb809",
    storageBucket: "marvel-quiz-cb809.appspot.com",
    messagingSenderId: "638632126431", 
    appId: "1:638632126431:web:afb718ab68c12db2f2466a"
  };



 class Firebase  {

  constructor() {
    app.initializeApp(config)
    this.auth = app.auth()   
  } 

  inscription =(email,password)=>{
    this.auth.createUserWithEmailAndPassword(email,password)
  }

  deconnexion =()=> this.auth.signOut()

}


export default Firebase
