import React,{useState,useContext} from 'react'
import {FirebaseContext} from '../Firebase/'
import {Link} from 'react-router-dom'

const data = {
    pseudo:'',
    email:'',
    password:'',
    confirmPassword:''
}

function Inscription(props) {
    console.log(props)
    // ds ma state je lui envoi mon objet data creer en amont
    const [loginData, setLoginData] = useState(data)
    // recuperation du message d'erreur si nous avons pas reussi a envoyer nos donner au back
    const [error, setError] = useState('')

    // destructuring de mes variabes qui se trouve ds ma state
    const {pseudo, email, password, confirmPassword} = loginData
    // appel de mon Context avec lequel j'ai fourni mes methodes firebase que j'ai declarer ds une classe
    const firebase = useContext(FirebaseContext)

const submit=(e)=>{
e.preventDefault()

firebase.inscription(email,password)
.then((user)=>{
    setError({...error})
    setLoginData({...loginData});
    props.history.push('/Welcome')
})
.catch((error)=>{
    setError(error)
    setLoginData({...loginData});
})
}

const valueInput=(e)=>{
setLoginData({...loginData,[e.target.id]: e.target.value})
}
// afficher mon bonton submit uniquement si mes var de mon state sont remplient
const dataState = pseudo !=='' && email !=='' && password !=='' && password === confirmPassword ? 
                   (<button>Inscription</button>) :  (<button disabled>Inscription</button>)
// affichage message d'erreur
// const errorMessage = error !== '' && (<span>{error}</span>)
return (
<div className='signUpLoginBox'>
<div className='slContainer'>
    <div className='formBoxLeftSignup'>

    </div>
    <div className='formBoxRight'>
        <form className='formContent' onSubmit={submit} >

            {/* {errorMessage} */}
            <h2>Inscription</h2>
            <div className='inputBox'>
                <input type='text' value={pseudo} id='pseudo' required  onChange={valueInput} />
                <label htmlFor='pseudo'>Pseudo</label>
            </div>
            <div className='inputBox'>
                <input type='mail' value={email} id='email' required onChange={valueInput} />
                <label htmlFor='email'>Email</label>
            </div>
            <div className='inputBox'>
                <input type='password' value={password} id='password' required onChange={valueInput} />
                <label htmlFor='password'>password</label>
            </div>
            <div className='inputBox'>
                <input type='password' value={confirmPassword} id='confirmPassword' required  onChange={valueInput}/>
                <label htmlFor='confirmPassword'>confirmer votre mot de passe</label>
            </div>

            <div>
                {dataState}
            </div>

        </form>
            <div className='linkContainer'>
                    <Link to='/Welcome' className='simpleLink'> d√©ja inscrit ? connectez-vous</Link>
            </div>
    </div>
</div>

</div>
)
}
export default Inscription
