import React from 'react'

function Question({answer,options,btn}) {


    const displayoption = options.map( (option,index) =>{
        return <p key={index} className='answerOptions'> {option }</p> })

    return (
        <div>
            <h2>{answer}</h2>
                {displayoption}
            <button disabled={btn} className='btnSubmit'>Suivant</button>
        </div>
    )
}

export default Question
