import React,{useEffect,useState,useRef} from 'react'

function Landing() {

    const refwolf = useRef(null)
    const refBtn = useRef(null)
    console.log('1/',refBtn)

    useEffect(()=>{
        refBtn.current.classList.remove('btn-welcome')
        setTimeout(() => {
        refwolf.current.classList.add('startingImg')
        refBtn.current.classList.add('btn-welcome')



        }, 3000);
    },[])


    return (
        <main ref={refwolf} className='welcomePage'>
            <div  className='leftBox'>
                <button  ref={refBtn} className='btn-welcome'>Inscription</button>
            </div>

            <div ref={refBtn} className='rightBox'>
                <button className='btn-welcome'>Connexion</button>
            </div>


        </main>
    )
}

export default Landing
