import React ,{useState,useEffect,useContext}from 'react'
import {FirebaseContext} from '../Firebase/'
import {Link}  from 'react-router-dom'


function Login(props) {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [error, setError] = useState('')
    const [btn, setBtn] = useState(false)

    useEffect(()=>{
       if(email.length > 9 && password.length > 5){
           setBtn(true)
       }else{
           setBtn(false)
       }
    },[email,password])

    const firebase = useContext(FirebaseContext)

    const submit =(e)=>{
        e.preventDefault()
        firebase.connexion(email,password)
        .then(user =>{
            props.history.push('/Welcome')
        }).catch(error =>{
            setError(error)
        })

    }
   const BtnFinish= btn ? (<button>Connexion</button>) :  (<button disabled>disabled</button>) 
   const errorMessage = error !=='' && <span>{error.message}</span>


    return (
        <div className='signUpLoginBox'>
            <div className='slContainer'>
                <div className='formBoxLeftLogin '>

                </div>
                    <div className='formBoxRight'>
                        <form className='formContent' onSubmit={submit} >

                            {errorMessage}
                            <h2>Connexion</h2>

                            <div className='inputBox'>
                                <input type='mail'  id='email' required onChange={e => setEmail(e.target.value)} />
                                <label htmlFor='email'>Email</label>
                            </div>
                            <div className='inputBox'>
                                <input type='password' id='password' required onChange={e => setPassword(e.target.value)}/>
                                <label htmlFor='password'>password</label>
                            </div>

                            <div>
                                {BtnFinish}
                            </div>

                        </form>
                            <div className='linkContainer'>
                                    <Link to='/Inscription' className='simpleLink'> Pas encore inscrit ? incrivez-vous</Link>
                            </div>
                    </div>

            </div>

        </div>
    )
}

export default Login
