import React,{Component} from 'react'
import Levels from '../Levels/index'
import ProgressBar from '../ProgressBar/index'
import Question from '../Question/index'
import {QuizMarvel} from '../QuizMarvel/index'

class Quiz extends Component {

    state={
       levelName:['debutant','confirme','expert'],
       QuizLevel: 0,
       maxQuestion: 10,
       storedQuestion:[],
       question: '',
       options: [],
       idQuestion: 0

    }

    loadQuestion= level =>{
        const fetchArrayQuiz = QuizMarvel[0].quizz[level]
        const lengthArray = fetchArrayQuiz.length

        if(lengthArray >= this.state.maxQuestion){

           const newArray = fetchArrayQuiz.map( ({answer, ...keepRest}) =>keepRest)
            this.setState({storedQuestion : newArray})

        }

    }  

    componentDidMount(){
        this.loadQuestion(this.state.levelName[this.state.QuizLevel])
    }

    componentDidUpdate(prevProps , prevState){
        if(this.state.storedQuestion !== prevState.storedQuestion){
            this.setState({
                question : this.state.storedQuestion[this.state.idQuestion].question,
                options : this.state.storedQuestion[this.state.idQuestion].option,
            })
        }
    }
    render(){


        return (
            <div>
                <Levels />
                <ProgressBar />
                <Question />
            </div> 
        )    

        }
 }
export default Quiz
